<span class="line"><span style="color:#89DDFF;">$</span><span style="color:#A6ACCD;">article</span><span style="color:#89DDFF;">-&gt;</span><span style="color:#A6ACCD;">content </span><span style="color:#89DDFF;">=</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">$</span><span style="color:#A6ACCD;">request</span><span style="color:#89DDFF;">-&gt;</span><span style="color:#A6ACCD;">content</span><span style="color:#89DDFF;">;</span></span>
